mapper.select:
SELECT distinct p.point "point", p.pointCheck "pointCheck", to_char(p.pointDate,'yyyy/mm/dd') "pointDate", a.STORENAME "storeName" FROM point p, admin a, member m where p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}
and a.storeId = #{storeId}
order by p.pointDate

SELECT distinct a.storeName "storeName", a.storeId "storeId" FROM point p, admin a, member m
p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}

SELECT PAYNUM "payNum", payday "payDay", storeName "storeName", REGEXP_REPLACE(LISTAGG (menuname, ',') WITHIN GROUP (ORDER BY menuname), '([^,]+)(,\1)+', '\1') "menuName" FROM orderAll_view
phone = #{phone}
GROUP BY payNum, payDay, storeName

select distinct u.serialNum "serialNum", u.useCheck "useCheck", a.storeName "storeName", to_char(c.expStart,'yyyy/mm/dd') "expStart", to_char(c.expEnd,'yyyy/mm/dd') "expEnd", c.discount "discount" from userCoupon u, coupon c, admin a, member m where u.serialnum = c.serialnum and c.storeid = a.storeid and u.membernum = m.membernum and c.expEnd > sysdate and u.usecheck = 0
and m.phone = #{phone}
and a.storeId = #{storeId}
order by a.storeName
