mapper.select:
SELECT distinct p.point "point", p.pointCheck "pointCheck", p.pointDate "pointDate", a.STORENAME "storeName" FROM point p, admin a, member m
p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}

SELECT distinct a.storeName "storeName" FROM point p, admin a, member m
p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}

SELECT distinct p.payDay "payDay", p.totalPay "totalPay", a.storeName "storeName" FROM payment p, admin a, member m
m.membernum = p.membernum
and m.phone = #{phone}

mapper.update:
UPDATE orderCpt
set
orderStart=#{orderStart}
where menuOrderNum= #{menuOrderNum}
