mapper.select:
SELECT distinct p.point "point", p.pointCheck "pointCheck", p.pointDate "pointDate", a.STORENAME "storeName" FROM point p, admin a, member m
p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}
and a.storeId = #{storeId}

SELECT distinct a.storeName "storeName", a.storeId "storeId" FROM point p, admin a, member m
p.storeId = a.storeId and m.membernum = p.membernum
and m.phone = #{phone}

SELECT distinct p.payDay "payDay", p.totalPay "totalPay", a.storeName "storeName" FROM payment p, admin a, member m, seat s where s.storeId = a.storeId and m.membernum = p.membernum and s.seatNum = p.seatNum
and m.phone = #{phone}
and a.storeId = #{storeId}

SELECT distinct menu.menuName "menuName", pay.payday "payday" FROM point p, admin a, member m, menu menu, payment pay where menu.storeId = a.storeId and m.membernum = p.membernum and p.storeId = a.storeId
and m.phone = #{phone}
and a.storeId = #{storeId}

mapper.update:
UPDATE orderCpt
set
orderStar=#{orderStar}
where menuOrderNum= #{menuOrderNum}

mapper.select:
select distinct u.serialNum "serialNum", u.memberNum "memberNum", u.useCheck "useCheck", a.storeName "storeName", c.expStart "expStart", c.expEnd "expEnd", c.discount from userCoupon u, admin a, member m, point p, coupon c where u.memberNum = 1 and m.membernum = p.membernum and p.storeId = a.storeId and c.storeId = a.storeId
and m.phone = #{phone}
and a.storeId = #{storeId}
