mapper.select:
select sum(point*pointCheck) AS point from point
storeId = #{storeId}
AND memberNum = (SELECT memberNum FROM member WHERE phone = #{phone})

SELECT c.serialNum "serialNum", c.expStart "expStart", to_char(c.expEnd,'yyyy/mm/dd') "expEnd", c.discount "discount", c.storeId "storeId", u.useCheck "useCheck", a.storeName "storeName", u.memberNum "memberNum" FROM USERCOUPON u INNER JOIN COUPON c ON (u.serialNum = c.serialNum) INNER JOIN ADMIN a ON (c.storeId = a.storeId)
c.expEnd > sysdate AND u.usecheck = 0 AND u.memberNum = (SELECT memberNum FROM member WHERE phone = #{phone})
AND c.storeId = #{storeId}
